import React, {useState} from 'react';
import axios from 'axios';


function App() {
  const [data,setData] = useState({});
  const [location, setLocation] = useState('');

  const url = `https://api.openweathermap.org/data/2.5/weather?${location}&units=imperial&appid=760108c5060d8d251dad95830b9b6256`;

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })
      setLocation('')
    }

  }

  return (
    <div className="app">
      <div className='search'>
        <input
        value={location}
        onChange={event => setLocation(event.target.value)}
        onKeyPress={searchLocation}
        placeholder='Enter Location'
        type='text'/>
      </div>
      <div className='container'>
        <div className='top'>
          <div className='location'>
            <p>{data.name}</p>
          </div>
          <div className='temp'>
            {data.main ? <h1>{data.main.temp.toFixed()}°F</h1> : null}
          </div>
          <div className='description'>
            <p>{data.weather ? <p>{data.weather[0].main}</p> : null}</p>
          </div>  
        </div>

        {data.name != undefined && 
          <div className='bottom'>
          <div className='feels'>
            {data.main ? <p>{data.main.feels_like} °F</p> : null}
            <p>Feels Like</p>
          </div>
          <div className='humidity'>
          {data.main ? <p>{data.main.humidity} %</p> : null}
          </div>
          <div className='wind'>
          {data.wind ? <p>{data.wind.speed} MPH</p> : null}
            <p>12 MPH</p>
          </div>
        </div>
        }      


      </div>

    </div>
  );
}

export default App;
